10‚Üíexport async function prepareUpload(fileName: string, extension: string) {
11‚Üí  const res = await fetch(`${API_BASE}/media-assets:prepare_upload`, {
12‚Üí    method: "POST",
13‚Üí    headers: headers(),
14‚Üí    body: JSON.stringify({
15‚Üí      file_name: fileName,
16‚Üí      extension,
17‚Üí      kind: "image",
18‚Üí    }),
19‚Üí  });
20‚Üí  if (!res.ok) throw new Error(`prepareUpload failed: ${res.status}`);
21‚Üí  return res.json() as Promise<{
22‚Üí    media_asset: { media_asset_id: string };
23‚Üí    upload_url: string;
24‚Üí  }>;
25‚Üí}
