27‚Üíexport async function uploadFile(uploadUrl: string, file: Blob, mimeType: string) {
28‚Üí  const res = await fetch(uploadUrl, {
29‚Üí    method: "PUT",
30‚Üí    headers: { "Content-Type": mimeType },
31‚Üí    body: file,
32‚Üí  });
33‚Üí  if (!res.ok) throw new Error(`uploadFile failed: ${res.status}`);
