43‚Üí    method: "POST",
44‚Üí    headers: headers(),
45‚Üí    body: JSON.stringify({
46‚Üí      display_name: params.displayName,
47‚Üí      world_prompt: {
48‚Üí        type: "image",
49‚Üí        image_prompt: {
50‚Üí          source: "media_asset",
51‚Üí          media_asset_id: params.mediaAssetId,
52‚Üí          is_pano: params.isPano,
53‚Üí        },
54‚Üí        ...(params.textPrompt ? { text_prompt: params.textPrompt } : {}),
55‚Üí      },
56‚Üí    }),
57‚Üí  });
58‚Üí  if (!res.ok) throw new Error(`generateWorld failed: ${res.status}`);
