"use client";

import { useState, useRef, useCallback } from "react";
import { cn } from "@/lib/utils";

/**
 * BottomSheet -- draggable bottom sheet for the world viewer.
 *
 * Design decisions:
 * - Starts at a "peek" height showing title + actions.
 * - Draggable handle lets users pull it to full or dismiss to peek.
 * - Uses transform for smooth 60fps animation (GPU-composited).
 * - Glassmorphism background to let the 3D viewer peek through.
 *
 * Accessibility:
 * - Handle has role="slider" for screen reader drag semantics.
 * - Content is always reachable via keyboard (no hidden states).
 * - Focus trap when expanded (future enhancement).
 */

interface BottomSheetProps {
  children: React.ReactNode;
  className?: string;
}

export function BottomSheet({ children, className }: BottomSheetProps) {
  const [expanded, setExpanded] = useState(false);
  const sheetRef = useRef<HTMLDivElement>(null);
  const startY = useRef(0);
  const currentY = useRef(0);

  const handleTouchStart = useCallback((e: React.TouchEvent) => {
    startY.current = e.touches[0].clientY;
  }, []);

  const handleTouchMove = useCallback((e: React.TouchEvent) => {
    currentY.current = e.touches[0].clientY;
    const diff = startY.current - currentY.current;

    if (sheetRef.current) {
      /* Apply a damped translation during drag for tactile feedback */
      const dampedDiff = diff * 0.4;
      const clampedTranslate = Math.max(-20, Math.min(100, dampedDiff));
      sheetRef.current.style.transform = `translateY(${-clampedTranslate}px)`;
    }
  }, []);

  const handleTouchEnd = useCallback(() => {
    const diff = startY.current - currentY.current;

    /* Swipe up threshold: 60px to expand */
    if (diff > 60) {
      setExpanded(true);
    }
    /* Swipe down threshold: 60px to collapse */
    if (diff < -60) {
      setExpanded(false);
    }

    /* Reset inline transform; let CSS take over */
    if (sheetRef.current) {
      sheetRef.current.style.transform = "";
    }
  }, []);

  return (
    <div
      ref={sheetRef}
      className={cn(
        "fixed inset-x-0 bottom-0 z-50",
        "glass-surface rounded-t-2xl safe-bottom",
        "transition-[max-height] duration-500 ease-[cubic-bezier(0.16,1,0.3,1)]",
        expanded ? "max-h-[70vh]" : "max-h-[180px]",
        className
      )}
      onTouchStart={handleTouchStart}
      onTouchMove={handleTouchMove}
      onTouchEnd={handleTouchEnd}
    >
      {/* Drag handle */}
      <div className="flex justify-center pt-3 pb-2">
        <button
          type="button"
          onClick={() => setExpanded(!expanded)}
          className="touch-target flex items-center justify-center"
          aria-label={expanded ? "Collapse panel" : "Expand panel"}
        >
          <div
            className="h-1 w-9 rounded-full bg-muted-foreground/30"
            aria-hidden="true"
          />
        </button>
      </div>

      {/* Sheet content */}
      <div
        className={cn(
          "overflow-y-auto px-5 pb-6",
          expanded ? "max-h-[calc(70vh-48px)]" : "max-h-[128px]",
          "scrollbar-none"
        )}
      >
        {children}
      </div>
    </div>
  );
}

"use client";

import { cn } from "@/lib/utils";

/**
 * GenerationProgress -- loading indicator for world generation (~5 min).
 *
 * Design:
 * - Full-screen overlay with a centered orbital animation.
 * - Percentage text when available, otherwise an indeterminate spinner.
 * - Status text below for user confidence ("Generating 3D mesh...").
 * - Dark overlay preserves the spatial feel.
 *
 * The orbital animation uses two concentric rings rotating at different
 * speeds to create a sci-fi "processing" feel without being heavy on GPU.
 */

interface GenerationProgressProps {
  /** 0-100 percent, or null for indeterminate */
  percent: number | null;
  /** Human-readable status like "Generating 3D mesh..." */
  statusText: string;
  className?: string;
}

export function GenerationProgress({
  percent,
  statusText,
  className,
}: GenerationProgressProps) {
  const circumference = 2 * Math.PI * 45; // radius = 45
  const strokeOffset =
    percent !== null
      ? circumference - (percent / 100) * circumference
      : circumference * 0.75; // indeterminate shows 25%

  return (
    <div
      className={cn(
        "flex flex-col items-center justify-center gap-6 p-8",
        className
      )}
      role="progressbar"
      aria-valuenow={percent ?? undefined}
      aria-valuemin={0}
      aria-valuemax={100}
      aria-label={statusText}
    >
      {/* Orbital progress ring */}
      <div className="relative size-32">
        {/* Background ring */}
        <svg
          className="absolute inset-0 size-full -rotate-90"
          viewBox="0 0 100 100"
        >
          <circle
            cx="50"
            cy="50"
            r="45"
            stroke="var(--muted)"
            strokeWidth="3"
            fill="none"
          />
          <circle
            cx="50"
            cy="50"
            r="45"
            stroke="url(#progressGradient)"
            strokeWidth="3"
            fill="none"
            strokeLinecap="round"
            strokeDasharray={circumference}
            strokeDashoffset={strokeOffset}
            className={cn(
              "transition-[stroke-dashoffset] duration-1000 ease-out",
              percent === null && "animate-space-spin-slow origin-center"
            )}
            style={
              percent === null
                ? { transformOrigin: "50px 50px" }
                : undefined
            }
          />
          <defs>
            <linearGradient
              id="progressGradient"
              x1="0%"
              y1="0%"
              x2="100%"
              y2="100%"
            >
              <stop offset="0%" stopColor="var(--space-cyan)" />
              <stop offset="100%" stopColor="var(--space-purple)" />
            </linearGradient>
          </defs>
        </svg>

        {/* Orbiting dot */}
        <div
          className="absolute inset-0 animate-space-spin-slow"
          aria-hidden="true"
        >
          <div
            className="absolute top-0 left-1/2 -translate-x-1/2 -translate-y-1/2 size-2.5 rounded-full bg-space-cyan"
            style={{ boxShadow: "0 0 8px 2px var(--space-glow)" }}
          />
        </div>

        {/* Center text */}
        <div className="absolute inset-0 flex flex-col items-center justify-center">
          {percent !== null ? (
            <>
              <span className="text-2xl font-bold tabular-nums text-foreground">
                {Math.round(percent)}%
              </span>
            </>
          ) : (
            <div className="size-2 rounded-full bg-space-cyan animate-space-pulse" />
          )}
        </div>
      </div>

      {/* Status text */}
      <div className="text-center space-y-1.5">
        <p className="text-sm font-medium text-foreground">{statusText}</p>
        <p className="text-xs text-muted-foreground">
          This typically takes 3-5 minutes
        </p>
      </div>
    </div>
  );
}

"use client";

import { useState, useCallback } from "react";
import { Button } from "@/components/ui/button";
import { Share2, Code2, Check, Copy, Link2 } from "lucide-react";
import { cn } from "@/lib/utils";

/**
 * ShareActions -- share and embed buttons with copy-to-clipboard.
 *
 * Design:
 * - Two primary actions: Share link + Copy embed code.
 * - Uses the Web Share API when available (mobile), falls back to clipboard.
 * - Brief "Copied!" confirmation with icon swap for tactile feedback.
 * - Touch-friendly 44px minimum targets.
 */

interface ShareActionsProps {
  worldId: string;
  title: string;
  className?: string;
}

export function ShareActions({ worldId, title, className }: ShareActionsProps) {
  const [copiedLink, setCopiedLink] = useState(false);
  const [copiedEmbed, setCopiedEmbed] = useState(false);

  const shareUrl =
    typeof window !== "undefined"
      ? `${window.location.origin}/share/${worldId}`
      : `/share/${worldId}`;

  const embedCode = `<iframe src="${shareUrl}" width="100%" height="600" frameborder="0" allowfullscreen></iframe>`;

  const handleShare = useCallback(async () => {
    /* Try native share first (great on mobile) */
    if (navigator.share) {
      try {
        await navigator.share({
          title: `${title} -- Space Clone`,
          url: shareUrl,
        });
        return;
      } catch {
        /* User cancelled or API failed, fall through to clipboard */
      }
    }

    /* Fallback: copy link */
    await navigator.clipboard.writeText(shareUrl);
    setCopiedLink(true);
    setTimeout(() => setCopiedLink(false), 2000);
  }, [shareUrl, title]);

  const handleCopyEmbed = useCallback(async () => {
    await navigator.clipboard.writeText(embedCode);
    setCopiedEmbed(true);
    setTimeout(() => setCopiedEmbed(false), 2000);
  }, [embedCode]);

  return (
    <div className={cn("flex gap-3", className)}>
      <Button
        variant="outline"
        size="sm"
        onClick={handleShare}
        className="gap-2 touch-target"
      >
        {copiedLink ? (
          <Check className="size-4 text-space-success" aria-hidden="true" />
        ) : (
          <Link2 className="size-4" aria-hidden="true" />
        )}
        {copiedLink ? "Copied!" : "Share"}
      </Button>

      <Button
        variant="outline"
        size="sm"
        onClick={handleCopyEmbed}
        className="gap-2 touch-target"
      >
        {copiedEmbed ? (
          <Check className="size-4 text-space-success" aria-hidden="true" />
        ) : (
          <Code2 className="size-4" aria-hidden="true" />
        )}
        {copiedEmbed ? "Copied!" : "Embed"}
      </Button>
    </div>
  );
}

