 * (getUserMedia, canvas, stream) will be added in a later iteration.
 *
 * Touch targets:
 * - Capture button: 72px diameter (well above 44px minimum)
 * - Cancel: 44px tap target
 * - Thumbnails: 56px each, scrollable
 *
 * Performance:
 * - Animations use transform/opacity only for GPU compositing.
 * - Thumbnail images will be base64 data URLs (no network round-trips).
 */

const TOTAL_POSITIONS = 16;

/** Direction labels for the guidance text */
const DIRECTION_LABELS = [
  "Front",
  "Front-Right",
  "Right-Front",
  "Right",
  "Right-Back",
  "Back-Right",
  "Back",
  "Back-Left",
  "Left-Back",
  "Left",
  "Left-Front",
  "Front-Left",
  "Front",
  "Front-Right",
  "Right-Front",
  "Right",
];

export default function CapturePage() {
  const router = useRouter();
  const [captured, setCaptured] = useState<string[]>([]);
  const [isCapturing, setIsCapturing] = useState(false);

  const currentIndex = captured.length;
  const isComplete = currentIndex >= TOTAL_POSITIONS;

  const handleCapture = useCallback(() => {
    if (isComplete || isCapturing) return;

    setIsCapturing(true);

    /*
     * Simulated capture: In production this would grab a frame from
     * the camera stream and encode it. For now we push a placeholder
     * gradient image encoded as a data URL.
     */
    setTimeout(() => {
      setCaptured((prev) => [
        ...prev,
        /* Placeholder: a numbered gradient square */
        `data:image/svg+xml,${encodeURIComponent(
          `<svg xmlns="http://www.w3.org/2000/svg" width="120" height="120">
            <defs>
              <linearGradient id="g" x1="0" y1="0" x2="1" y2="1">
                <stop offset="0%" stop-color="hsl(${(prev.length * 22.5) % 360},70%,30%)"/>
                <stop offset="100%" stop-color="hsl(${((prev.length * 22.5) + 40) % 360},70%,20%)"/>
              </linearGradient>
            </defs>
            <rect width="120" height="120" fill="url(#g)"/>
            <text x="60" y="68" text-anchor="middle" fill="white" font-size="32" font-family="system-ui">${prev.length + 1}</text>
          </svg>`
        )}`,
      ]);
      setIsCapturing(false);
    }, 200);
  }, [isComplete, isCapturing]);

  const handleReset = useCallback(() => {
    setCaptured([]);
  }, []);

  const handleDone = useCallback(() => {
    /*
     * In production: upload photos, start stitching, navigate to
     * a loading/progress screen. For now, go to gallery.
     */
    router.push("/gallery");
  }, [router]);

  return (
    <div className="relative flex h-dvh flex-col bg-black safe-top safe-bottom overflow-hidden">
