"use client";

import { useState, useCallback } from "react";
import { useRouter } from "next/navigation";
import { Button } from "@/components/ui/button";
import { CaptureProgress } from "@/components/capture/capture-progress";
import { X, ChevronRight, RotateCcw } from "lucide-react";
import { cn } from "@/lib/utils";

/**
 * Camera Capture Screen
 * =====================
 * Full-screen mobile-first camera UI for capturing 16 photos in a 360-degree sweep.
 *
 * Layout (top to bottom):
 * 1. Top bar: Cancel button (left) + photo counter (right)
 * 2. Camera viewfinder: full bleed, takes remaining vertical space
 * 3. Overlay: Compass rose progress indicator (top-center of viewfinder)
 * 4. Direction hint arrow
 * 5. Bottom bar: Thumbnail strip + Capture button
 *
 * The viewfinder is a placeholder <div> right now. Camera integration
 * (getUserMedia, canvas, stream) will be added in a later iteration.
 *
 * Touch targets:
 * - Capture button: 72px diameter (well above 44px minimum)
 * - Cancel: 44px tap target
 * - Thumbnails: 56px each, scrollable
 *
 * Performance:
 * - Animations use transform/opacity only for GPU compositing.
 * - Thumbnail images will be base64 data URLs (no network round-trips).
 */

const TOTAL_POSITIONS = 16;

/** Direction labels for the guidance text */
const DIRECTION_LABELS = [
  "Front",
  "Front-Right",
  "Right-Front",
  "Right",
  "Right-Back",
  "Back-Right",
  "Back",
  "Back-Left",
  "Left-Back",
  "Left",
  "Left-Front",
  "Front-Left",
  "Front",
  "Front-Right",
  "Right-Front",
  "Right",
];

export default function CapturePage() {
  const router = useRouter();
  const [captured, setCaptured] = useState<string[]>([]);
  const [isCapturing, setIsCapturing] = useState(false);

  const currentIndex = captured.length;
  const isComplete = currentIndex >= TOTAL_POSITIONS;

  const handleCapture = useCallback(() => {
    if (isComplete || isCapturing) return;

    setIsCapturing(true);

    /*
     * Simulated capture: In production this would grab a frame from
     * the camera stream and encode it. For now we push a placeholder
     * gradient image encoded as a data URL.
     */
    setTimeout(() => {
      setCaptured((prev) => [
        ...prev,
        /* Placeholder: a numbered gradient square */
        `data:image/svg+xml,${encodeURIComponent(
          `<svg xmlns="http://www.w3.org/2000/svg" width="120" height="120">
            <defs>
              <linearGradient id="g" x1="0" y1="0" x2="1" y2="1">
                <stop offset="0%" stop-color="hsl(${(prev.length * 22.5) % 360},70%,30%)"/>
                <stop offset="100%" stop-color="hsl(${((prev.length * 22.5) + 40) % 360},70%,20%)"/>
              </linearGradient>
            </defs>
            <rect width="120" height="120" fill="url(#g)"/>
            <text x="60" y="68" text-anchor="middle" fill="white" font-size="32" font-family="system-ui">${prev.length + 1}</text>
          </svg>`
        )}`,
      ]);
      setIsCapturing(false);
    }, 200);
  }, [isComplete, isCapturing]);

  const handleReset = useCallback(() => {
    setCaptured([]);
  }, []);

  const handleDone = useCallback(() => {
    /*
     * In production: upload photos, start stitching, navigate to
     * a loading/progress screen. For now, go to gallery.
     */
    router.push("/gallery");
  }, [router]);

  return (
    <div className="relative flex h-dvh flex-col bg-black safe-top safe-bottom overflow-hidden">
      {/* ============================================================
          Top bar -- always visible over the viewfinder
          ============================================================ */}
      <header className="absolute top-0 inset-x-0 z-30 flex items-center justify-between px-4 pt-3 safe-top">
        {/* Cancel */}
        <Button
          variant="ghost"
          size="icon"
          onClick={() => router.back()}
          className="touch-target rounded-full bg-black/40 backdrop-blur-sm text-white hover:bg-black/60"
          aria-label="Cancel capture"
        >
          <X className="size-5" />
        </Button>

        {/* Counter pill */}
        <div className="flex items-center gap-2 rounded-full bg-black/40 backdrop-blur-sm px-3.5 py-1.5">
          <span className="text-sm font-semibold tabular-nums text-white">
            {currentIndex}/{TOTAL_POSITIONS}
          </span>
        </div>

        {/* Reset (visible when at least 1 photo taken) */}
        {captured.length > 0 && !isComplete && (
          <Button
            variant="ghost"
            size="icon"
            onClick={handleReset}
            className="touch-target rounded-full bg-black/40 backdrop-blur-sm text-white hover:bg-black/60"
            aria-label="Reset all captures"
          >
            <RotateCcw className="size-4" />
          </Button>
        )}
      </header>

      {/* ============================================================
          Camera viewfinder (placeholder)
          ============================================================ */}
      <div
        className="flex-1 relative bg-gradient-to-br from-[oklch(0.08_0.015_260)] via-[oklch(0.06_0.02_280)] to-[oklch(0.05_0.01_240)]"
        aria-label="Camera viewfinder"
        role="img"
      >
        {/* Subtle grid overlay to hint at spatial awareness */}
        <div
          className="absolute inset-0 opacity-[0.04]"
          aria-hidden="true"
          style={{
            backgroundImage:
              "linear-gradient(var(--space-cyan) 1px, transparent 1px), linear-gradient(90deg, var(--space-cyan) 1px, transparent 1px)",
            backgroundSize: "48px 48px",
          }}
        />

        {/* Center crosshair */}
        <div className="absolute inset-0 flex items-center justify-center pointer-events-none" aria-hidden="true">
          <div className="relative size-16">
            {/* Horizontal line */}
            <div className="absolute top-1/2 left-0 w-full h-px bg-white/20" />
            {/* Vertical line */}
            <div className="absolute left-1/2 top-0 h-full w-px bg-white/20" />
            {/* Center dot */}
            <div className="absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 size-1.5 rounded-full bg-white/40" />
          </div>
        </div>

        {/* Compass progress - positioned in the upper portion of viewfinder */}
        <div className="absolute top-8 left-1/2 -translate-x-1/2 safe-top">
          <CaptureProgress
            captured={currentIndex}
            activeIndex={isComplete ? null : currentIndex}
            size={160}
          />
        </div>

        {/* Direction guidance text */}
        {!isComplete && (
          <div className="absolute bottom-32 inset-x-0 flex flex-col items-center gap-1 animate-space-fade-in">
            <div className="flex items-center gap-2 rounded-full bg-black/50 backdrop-blur-sm px-4 py-2">
              <span className="text-xs font-medium text-white/80">
                Point camera
              </span>
              <ChevronRight className="size-3.5 text-space-cyan" aria-hidden="true" />
              <span className="text-xs font-semibold text-space-cyan">
                {DIRECTION_LABELS[currentIndex] ?? "Complete"}
              </span>
            </div>
          </div>
        )}

        {/* Completion overlay */}
        {isComplete && (
          <div className="absolute inset-0 flex flex-col items-center justify-center bg-black/60 backdrop-blur-sm animate-space-fade-in z-20">
            <div className="space-y-4 text-center px-6">
              <div className="inline-flex size-16 items-center justify-center rounded-full bg-space-success/20 border border-space-success/30">
                <svg
                  className="size-8 text-space-success"
                  fill="none"
                  viewBox="0 0 24 24"
                  strokeWidth={2}
                  stroke="currentColor"
                  aria-hidden="true"
                >
                  <path strokeLinecap="round" strokeLinejoin="round" d="M5 13l4 4L19 7" />
                </svg>
              </div>
              <h2 className="text-xl font-bold text-white">All 16 captured!</h2>
              <p className="text-sm text-white/60">
                Ready to generate your 3D world
              </p>
              <Button size="lg" onClick={handleDone} className="mt-2 gap-2 touch-target">
                Generate World
                <ChevronRight className="size-4" />
              </Button>
            </div>
          </div>
        )}
      </div>

      {/* ============================================================
          Bottom controls: thumbnail strip + capture button
          ============================================================ */}
      <div className="relative z-30 bg-gradient-to-t from-black via-black/95 to-transparent pt-6 pb-4 px-4 safe-bottom">
        {/* Thumbnail strip */}
        {captured.length > 0 && (
          <div
            className="mb-4 flex gap-2 overflow-x-auto scrollbar-none pb-1 -mx-1 px-1"
            role="list"
            aria-label="Captured photos"
          >
            {captured.map((src, i) => (
              <div
                key={i}
                role="listitem"
                className={cn(
                  "shrink-0 size-14 rounded-lg overflow-hidden border-2 transition-all duration-200",
                  "animate-space-scale-in",
                  i === captured.length - 1
                    ? "border-space-cyan glow-ring"
                    : "border-white/10"
                )}
                style={{ animationDelay: `${i * 30}ms` }}
              >
                <img
                  src={src}
                  alt={`Photo ${i + 1}`}
                  className="size-full object-cover"
                />
              </div>
            ))}
          </div>
        )}

        {/* Capture button row */}
        {!isComplete && (
          <div className="flex items-center justify-center">
            <button
              type="button"
              onClick={handleCapture}
              disabled={isCapturing}
              className={cn(
                /* Outer ring */
                "relative size-[72px] rounded-full",
                "border-[3px] border-white/80",
                "transition-all duration-150 active:scale-95",
                "focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-space-cyan focus-visible:ring-offset-2 focus-visible:ring-offset-black",
                "disabled:opacity-50 disabled:active:scale-100"
              )}
              aria-label={`Capture photo ${currentIndex + 1} of ${TOTAL_POSITIONS}`}
            >
              {/* Inner filled circle */}
              <div
                className={cn(
                  "absolute inset-[4px] rounded-full bg-white transition-all duration-150",
                  isCapturing && "scale-90 bg-white/70"
                )}
              />

              {/* Subtle pulse glow when ready to capture */}
              {!isCapturing && (
                <div
                  className="absolute -inset-1 rounded-full border border-space-cyan/30 animate-space-pulse"
                  aria-hidden="true"
                />
              )}
            </button>
          </div>
        )}
      </div>
    </div>
  );
}

