import { cn } from "@/lib/utils";

const TOTAL_POSITIONS = 16;

const DIRECTION_LABELS = [
  "Front",
  "Front-Right",
  "Right-Front",
  "Right",
  "Right-Back",
  "Back-Right",
  "Back",
  "Back-Left",
  "Left-Back",
  "Left",
  "Left-Front",
  "Front-Left",
  "Front",
  "Front-Right",
  "Right-Front",
  "Right",
];

