267‚Üí  }
268‚Üí}
269‚Üí
270‚Üí@keyframes space-slide-up {
271‚Üí  from {
272‚Üí    opacity: 0;
273‚Üí    transform: translateY(100%);
274‚Üí  }
275‚Üí  to {
276‚Üí    opacity: 1;
277‚Üí    transform: translateY(0);
278‚Üí  }
279‚Üí}
280‚Üí
281‚Üí@keyframes space-scale-in {
282‚Üí  from {
283‚Üí    opacity: 0;
284‚Üí    transform: scale(0.9);
285‚Üí  }
286‚Üí  to {
287‚Üí    opacity: 1;
288‚Üí    transform: scale(1);
289‚Üí  }
290‚Üí}
291‚Üí
292‚Üí@keyframes space-progress-ring {
293‚Üí  0% {
294‚Üí    stroke-dashoffset: 283;
295‚Üí  }
296‚Üí  100% {
297‚Üí    stroke-dashoffset: 0;
298‚Üí  }
299‚Üí}
300‚Üí
301‚Üí/* ==========================================================================
302‚Üí   Utility classes for spatial design
303‚Üí   ========================================================================== */
304‚Üí@layer utilities {
305‚Üí  .animate-space-pulse {
306‚Üí    animation: space-pulse 2s ease-in-out infinite;
307‚Üí  }
308‚Üí  .animate-space-glow {
309‚Üí    animation: space-glow 2s ease-in-out infinite;
310‚Üí  }
311‚Üí  .animate-space-spin-slow {
312‚Üí    animation: space-spin-slow 8s linear infinite;
313‚Üí  }
314‚Üí  .animate-space-orbit {
315‚Üí    animation: space-orbit 4s linear infinite;
316‚Üí  }
317‚Üí  .animate-space-fade-in {
318‚Üí    animation: space-fade-in 0.3s ease-out;
319‚Üí  }
320‚Üí  .animate-space-slide-up {
321‚Üí    animation: space-slide-up 0.4s cubic-bezier(0.16, 1, 0.3, 1);
322‚Üí  }
323‚Üí  .animate-space-scale-in {
324‚Üí    animation: space-scale-in 0.2s ease-out;
325‚Üí  }
326‚Üí
327‚Üí  /* Glassmorphism surface */
328‚Üí  .glass-surface {
329‚Üí    background: oklch(0.14 0.02 260 / 60%);
330‚Üí    backdrop-filter: blur(20px) saturate(180%);
331‚Üí    -webkit-backdrop-filter: blur(20px) saturate(180%);
332‚Üí    border: 1px solid oklch(0.95 0.02 220 / 8%);
333‚Üí  }
334‚Üí
335‚Üí  /* Glow ring for active/focused elements */
336‚Üí  .glow-ring {
337‚Üí    box-shadow: 0 0 0 1px var(--space-glow),
338‚Üí                0 0 12px 0 var(--space-glow);
339‚Üí  }
340‚Üí
341‚Üí  /* Gradient text for headings */
342‚Üí  .text-gradient-space {
343‚Üí    background: linear-gradient(135deg,
344‚Üí      oklch(0.78 0.18 200),
345‚Üí      oklch(0.7 0.17 260),
346‚Üí      oklch(0.72 0.19 290)
347‚Üí    );
348‚Üí    -webkit-background-clip: text;
349‚Üí    background-clip: text;
350‚Üí    -webkit-text-fill-color: transparent;
351‚Üí  }
352‚Üí
353‚Üí  /* Safe area padding for PWA */
354‚Üí  .safe-top {
355‚Üí    padding-top: env(safe-area-inset-top, 0px);
356‚Üí  }
357‚Üí  .safe-bottom {
358‚Üí    padding-bottom: env(safe-area-inset-bottom, 0px);
359‚Üí  }
360‚Üí  .safe-left {
361‚Üí    padding-left: env(safe-area-inset-left, 0px);
362‚Üí  }
363‚Üí  .safe-right {
364‚Üí    padding-right: env(safe-area-inset-right, 0px);
365‚Üí  }
366‚Üí
367‚Üí  /* Touch-friendly minimum target */
368‚Üí  .touch-target {
369‚Üí    min-width: 44px;
370‚Üí    min-height: 44px;
371‚Üí  }
372‚Üí
373‚Üí  /* Hide scrollbar but keep scrolling */
374‚Üí  .scrollbar-none {
375‚Üí    -ms-overflow-style: none;
376‚Üí    scrollbar-width: none;
377‚Üí  }
378‚Üí  .scrollbar-none::-webkit-scrollbar {
379‚Üí    display: none;
380‚Üí  }
381‚Üí}
382‚Üí

