  3‚Üíimport { useState, useEffect, useCallback } from "react";
  4‚Üíimport { useParams, useRouter } from "next/navigation";
  5‚Üíimport { Button } from "@/components/ui/button";
  6‚Üíimport { BottomSheet } from "@/components/viewer/bottom-sheet";
  7‚Üíimport { GenerationProgress } from "@/components/viewer/generation-progress";
  8‚Üíimport { ShareActions } from "@/components/share/share-actions";
  9‚Üíimport { ArrowLeft, ExternalLink, Eye } from "lucide-react";
 10‚Üíimport type { WorldRecord } from "@/lib/types";
 11‚Üí
 12‚Üíconst GENERATION_STAGES = [
 13‚Üí  "Uploading panorama...",
 14‚Üí  "Analyzing scene geometry...",
 15‚Üí  "Generating depth map...",
 16‚Üí  "Building 3D point cloud...",
 17‚Üí  "Creating Gaussian splats...",
 18‚Üí  "Generating mesh...",
 19‚Üí  "Optimizing textures...",
 20‚Üí  "Finalizing world...",
 21‚Üí];
 22‚Üí
 23‚Üíexport default function WorldViewerPage() {
 24‚Üí  const params = useParams<{ id: string }>();
 25‚Üí  const router = useRouter();
 26‚Üí
 27‚Üí  const [world, setWorld] = useState<WorldRecord | null>(null);
 28‚Üí  const [loading, setLoading] = useState(true);
 29‚Üí  const [iframeLoaded, setIframeLoaded] = useState(false);
 30‚Üí  const [genPercent, setGenPercent] = useState<number | null>(null);
 31‚Üí  const [genStageText, setGenStageText] = useState("Processing...");
 32‚Üí
 33‚Üí  // Fetch world data
 34‚Üí  const fetchWorld = useCallback(async () => {
 35‚Üí    try {
 36‚Üí      const res = await fetch(`/api/worlds/${params.id}`);
 37‚Üí      if (res.ok) {
 38‚Üí        const data = await res.json();
 39‚Üí        setWorld(data.world);
 40‚Üí      }
 41‚Üí    } catch {
 42‚Üí      // Will show not-found state
 43‚Üí    } finally {
 44‚Üí      setLoading(false);
 45‚Üí    }
 46‚Üí  }, [params.id]);
 47‚Üí
 48‚Üí  useEffect(() => {
 49‚Üí    fetchWorld();
 50‚Üí  }, [fetchWorld]);
 51‚Üí
 52‚Üí  // Poll generation status
 53‚Üí  useEffect(() => {
 54‚Üí    if (!world || (world.status !== "generating" && world.status !== "pending")) return;
 55‚Üí
 56‚Üí    const poll = async () => {
 57‚Üí      try {
 58‚Üí        const res = await fetch(`/api/generate/status?worldId=${world.id}`);
 59‚Üí        if (!res.ok) return;
 60‚Üí
 61‚Üí        const data = await res.json();
 62‚Üí
 63‚Üí        if (data.status === "ready" && data.world) {
 64‚Üí          setWorld(data.world);
 65‚Üí        } else if (data.status === "failed") {
 66‚Üí          setWorld((w) => w ? { ...w, status: "failed" } : w);
 67‚Üí        } else if (data.progress) {
 68‚Üí          const pct = data.progress.percent;
 69‚Üí          setGenPercent(pct ?? null);
 70‚Üí          if (pct !== null) {
 71‚Üí            const stageIdx = Math.min(
 72‚Üí              Math.floor((pct / 100) * GENERATION_STAGES.length),
 73‚Üí              GENERATION_STAGES.length - 1
 74‚Üí            );
 75‚Üí            setGenStageText(GENERATION_STAGES[stageIdx]);
 76‚Üí          }
 77‚Üí        }
 78‚Üí      } catch {
 79‚Üí        // Retry on next interval
 80‚Üí      }
 81‚Üí    };
 82‚Üí
 83‚Üí    poll();
 84‚Üí    const interval = setInterval(poll, 15000);
 85‚Üí    return () => clearInterval(interval);
 86‚Üí  }, [world?.id, world?.status]);
 87‚Üí
 88‚Üí  // Loading
 89‚Üí  if (loading) {
 90‚Üí    return (
 91‚Üí      <div className="flex h-dvh items-center justify-center bg-background">
 92‚Üí        <div className="flex flex-col items-center gap-3">
 93‚Üí          <div className="size-8 rounded-full border-2 border-primary border-t-transparent animate-spin" />
 94‚Üí          <span className="text-sm text-muted-foreground">Loading world...</span>
 95‚Üí        </div>
 96‚Üí      </div>
 97‚Üí    );
 98‚Üí  }
 99‚Üí
100‚Üí  if (!world) {
101‚Üí    return (
102‚Üí      <div className="flex h-dvh flex-col items-center justify-center gap-4 bg-background p-6">
103‚Üí        <p className="text-muted-foreground">World not found</p>
104‚Üí        <Button variant="outline" onClick={() => router.push("/gallery")}>
105‚Üí          Back to Gallery
106‚Üí        </Button>
107‚Üí      </div>
108‚Üí    );
109‚Üí  }
110‚Üí
111‚Üí  // Generating state
112‚Üí  if (world.status === "generating" || world.status === "pending") {
113‚Üí    return (
114‚Üí      <div className="flex h-dvh flex-col bg-background safe-top safe-bottom">
115‚Üí        <header className="flex items-center px-4 pt-3 safe-top">
116‚Üí          <Button
117‚Üí            variant="ghost"
118‚Üí            size="sm"
119‚Üí            onClick={() => router.push("/gallery")}
120‚Üí            className="gap-2 touch-target"
121‚Üí          >
122‚Üí            <ArrowLeft className="size-4" />
123‚Üí            Gallery
124‚Üí          </Button>
125‚Üí        </header>
126‚Üí
127‚Üí        <div className="flex-1 flex flex-col items-center justify-center">
128‚Üí          <GenerationProgress
129‚Üí            percent={genPercent}
130‚Üí            statusText={
131‚Üí              world.status === "pending"
132‚Üí                ? "Waiting in queue..."
133‚Üí                : genStageText
134‚Üí            }
135‚Üí          />
136‚Üí          <h2 className="mt-4 text-lg font-semibold text-foreground text-center px-6">
137‚Üí            {world.title}
138‚Üí          </h2>
139‚Üí        </div>
140‚Üí      </div>
141‚Üí    );
142‚Üí  }
143‚Üí
144‚Üí  // Failed state
145‚Üí  if (world.status === "failed") {
146‚Üí    return (
147‚Üí      <div className="flex h-dvh flex-col items-center justify-center gap-6 bg-background p-6 safe-top safe-bottom">
148‚Üí        <div className="flex size-16 items-center justify-center rounded-full bg-destructive/10 border border-destructive/20">
149‚Üí          <svg
150‚Üí            className="size-8 text-destructive"
151‚Üí            fill="none"
152‚Üí            viewBox="0 0 24 24"
153‚Üí            strokeWidth={1.5}
154‚Üí            stroke="currentColor"
155‚Üí          >
156‚Üí            <path strokeLinecap="round" strokeLinejoin="round" d="M12 9v3.75m9-.75a9 9 0 11-18 0 9 9 0 0118 0zm-9 3.75h.008v.008H12v-.008z" />
157‚Üí          </svg>
158‚Üí        </div>
159‚Üí        <div className="text-center space-y-2">
160‚Üí          <h2 className="text-lg font-semibold text-foreground">Generation Failed</h2>
161‚Üí          <p className="text-sm text-muted-foreground max-w-xs">
162‚Üí            Something went wrong. Try capturing again with better lighting.
163‚Üí          </p>
164‚Üí        </div>
165‚Üí        <div className="flex gap-3">
166‚Üí          <Button variant="outline" onClick={() => router.push("/gallery")}>
167‚Üí            Back to Gallery
168‚Üí          </Button>
169‚Üí          <Button onClick={() => router.push("/capture")}>
170‚Üí            Try Again
171‚Üí          </Button>
172‚Üí        </div>
173‚Üí      </div>
174‚Üí    );
175‚Üí  }
176‚Üí
177‚Üí  // Ready state ‚Äî full-screen viewer
178‚Üí  return (
179‚Üí    <div className="relative h-dvh bg-black overflow-hidden">
180‚Üí      {!iframeLoaded && (
181‚Üí        <div className="absolute inset-0 z-20 flex items-center justify-center bg-background">
182‚Üí          <div className="flex flex-col items-center gap-3">
183‚Üí            <div className="size-8 rounded-full border-2 border-primary border-t-transparent animate-spin" />
184‚Üí            <span className="text-sm text-muted-foreground">Loading 3D viewer...</span>
185‚Üí          </div>
186‚Üí        </div>
187‚Üí      )}
188‚Üí
189‚Üí      <iframe
190‚Üí        src={world.viewer_url ?? "about:blank"}
191‚Üí        className="absolute inset-0 h-full w-full border-0"
192‚Üí        allow="accelerometer; gyroscope; fullscreen"
193‚Üí        allowFullScreen
194‚Üí        onLoad={() => setIframeLoaded(true)}
195‚Üí        title={`3D view of ${world.title}`}
196‚Üí      />
197‚Üí
198‚Üí      <div className="absolute top-3 left-4 z-30 safe-top">
199‚Üí        <Button
200‚Üí          variant="ghost"
201‚Üí          size="icon"
202‚Üí          onClick={() => router.push("/gallery")}
203‚Üí          className="touch-target rounded-full bg-black/40 backdrop-blur-sm text-white hover:bg-black/60"
204‚Üí          aria-label="Back to gallery"
205‚Üí        >
206‚Üí          <ArrowLeft className="size-5" />
207‚Üí        </Button>
208‚Üí      </div>
209‚Üí
210‚Üí      <div className="absolute top-3 right-4 z-30 safe-top">
211‚Üí        <div className="flex items-center gap-1.5 rounded-full bg-black/40 backdrop-blur-sm px-3 py-1.5">
