  5‚Üíexport interface DeviceOrientationState {
  6‚Üí  /** Compass heading in degrees (0-360), null if unavailable */
  7‚Üí  heading: number | null;
  8‚Üí  /** Whether orientation tracking is active */
  9‚Üí  isTracking: boolean;
