 16‚Üíexport function useDeviceOrientation(): DeviceOrientationState {
 17‚Üí  const [heading, setHeading] = useState<number | null>(null);
 18‚Üí  const [isTracking, setIsTracking] = useState(false);
 19‚Üí  const [permissionNeeded, setPermissionNeeded] = useState(false);
 20‚Üí  const lastHeading = useRef<number | null>(null);
 21‚Üí
 22‚Üí  const handleOrientation = useCallback((event: DeviceOrientationEvent) => {
 23‚Üí    // webkitCompassHeading is Safari-specific (iOS)
 24‚Üí    const compassHeading =
 25‚Üí      (event as DeviceOrientationEvent & { webkitCompassHeading?: number })
 26‚Üí        .webkitCompassHeading ?? null;
 27‚Üí
 28‚Üí    // alpha is the compass direction (0-360) on Android/Chrome
 29‚Üí    const alpha = event.alpha;
 30‚Üí
 31‚Üí    let newHeading: number | null = null;
 32‚Üí
 33‚Üí    if (compassHeading !== null && compassHeading !== undefined) {
 34‚Üí      newHeading = compassHeading;
 35‚Üí    } else if (alpha !== null && alpha !== undefined) {
 36‚Üí      // On Android, alpha goes counter-clockwise, compass is clockwise
 37‚Üí      newHeading = (360 - alpha) % 360;
 38‚Üí    }
 39‚Üí
 40‚Üí    if (newHeading !== null) {
 41‚Üí      // Only update if heading changed significantly (reduce re-renders)
 42‚Üí      const prev = lastHeading.current;
 43‚Üí      if (prev === null || Math.abs(newHeading - prev) > 2) {
 44‚Üí        lastHeading.current = newHeading;
 45‚Üí        setHeading(Math.round(newHeading));
 46‚Üí        setIsTracking(true);
 47‚Üí      }
 48‚Üí    }
 49‚Üí  }, []);
 50‚Üí
 51‚Üí  const requestPermission = useCallback(async () => {
 52‚Üí    // iOS 13+ requires explicit permission request
 53‚Üí    const DOE = DeviceOrientationEvent as unknown as {
 54‚Üí      requestPermission?: () => Promise<string>;
 55‚Üí    };
 56‚Üí
 57‚Üí    if (typeof DOE.requestPermission === "function") {
 58‚Üí      try {
 59‚Üí        const permission = await DOE.requestPermission();
 60‚Üí        if (permission === "granted") {
 61‚Üí          window.addEventListener("deviceorientation", handleOrientation, true);
 62‚Üí          setPermissionNeeded(false);
 63‚Üí        }
 64‚Üí      } catch {
 65‚Üí        // User denied
 66‚Üí      }
 67‚Üí    }
 68‚Üí  }, [handleOrientation]);
 69‚Üí
 70‚Üí  useEffect(() => {
 71‚Üí    // Check if DeviceOrientationEvent.requestPermission exists (iOS 13+)
 72‚Üí    const DOE = DeviceOrientationEvent as unknown as {
 73‚Üí      requestPermission?: () => Promise<string>;
 74‚Üí    };
 75‚Üí
 76‚Üí    if (typeof DOE.requestPermission === "function") {
 77‚Üí      // iOS ‚Äî need to request permission first
 78‚Üí      setPermissionNeeded(true);
 79‚Üí    } else {
 80‚Üí      // Android/desktop ‚Äî just listen
 81‚Üí      window.addEventListener("deviceorientation", handleOrientation, true);
 82‚Üí    }
 83‚Üí
 84‚Üí    return () => {
 85‚Üí      window.removeEventListener("deviceorientation", handleOrientation, true);
 86‚Üí    };
 87‚Üí  }, [handleOrientation]);
 88‚Üí
 89‚Üí  return { heading, isTracking, permissionNeeded, requestPermission };
 90‚Üí}
