 92‚Üí/**
 93‚Üí * Given a heading (0-360) and the target slot (0-15),
 94‚Üí * returns whether the user is pointing roughly at that slot.
 95‚Üí * Each slot covers 22.5 degrees.
 96‚Üí */
 97‚Üíexport function isAlignedWithSlot(
 98‚Üí  heading: number,
 99‚Üí  startHeading: number,
100‚Üí  slotIndex: number,
101‚Üí  tolerance: number = 15
102‚Üí): boolean {
103‚Üí  const slotAngle = (slotIndex * 22.5) % 360;
104‚Üí  const targetHeading = (startHeading + slotAngle) % 360;
105‚Üí  let diff = Math.abs(heading - targetHeading);
106‚Üí  if (diff > 180) diff = 360 - diff;
107‚Üí  return diff <= tolerance;
108‚Üí}
