  1‚Üíimport { NextResponse } from "next/server";
  2‚Üíimport { createClient } from "@/lib/supabase/server";
  3‚Üí
  4‚Üíexport async function POST(request: Request) {
  5‚Üí  const supabase = await createClient();
  6‚Üí  const { data: { user } } = await supabase.auth.getUser();
  7‚Üí
  8‚Üí  if (!user) {
  9‚Üí    return NextResponse.json({ error: "Unauthorized" }, { status: 401 });
 10‚Üí  }
 11‚Üí
 12‚Üí  const { sessionId } = await request.json();
 13‚Üí
 14‚Üí  if (!sessionId) {
 15‚Üí    return NextResponse.json({ error: "sessionId required" }, { status: 400 });
 16‚Üí  }
 17‚Üí
 18‚Üí  // Get session details
 19‚Üí  const { data: session } = await supabase
 20‚Üí    .from("capture_sessions")
 21‚Üí    .select("*")
 22‚Üí    .eq("id", sessionId)
 23‚Üí    .eq("user_id", user.id)
 24‚Üí    .single();
 25‚Üí
 26‚Üí  if (!session) {
 27‚Üí    return NextResponse.json({ error: "Session not found" }, { status: 404 });
 28‚Üí  }
 29‚Üí
 30‚Üí  // Update status to stitching
 31‚Üí  await supabase
 32‚Üí    .from("capture_sessions")
 33‚Üí    .update({ status: "stitching" })
 34‚Üí    .eq("id", sessionId);
 35‚Üí
 36‚Üí  try {
 37‚Üí    // List photos in storage
 38‚Üí    const { data: files } = await supabase.storage
 39‚Üí      .from("photos")
 40‚Üí      .list(session.photos_storage_path);
 41‚Üí
 42‚Üí    if (!files || files.length === 0) {
 43‚Üí      throw new Error("No photos found in storage");
 44‚Üí    }
 45‚Üí
 46‚Üí    // Download photos and send to stitch service
 47‚Üí    const formData = new FormData();
 48‚Üí
 49‚Üí    for (const file of files.sort((a, b) => a.name.localeCompare(b.name))) {
 50‚Üí      const { data: blob } = await supabase.storage
 51‚Üí        .from("photos")
 52‚Üí        .download(`${session.photos_storage_path}/${file.name}`);
 53‚Üí
 54‚Üí      if (blob) {
 55‚Üí        formData.append("files", blob, file.name);
 56‚Üí      }
 57‚Üí    }
 58‚Üí
 59‚Üí    // Call Python stitch service
 60‚Üí    const stitchUrl = process.env.STITCH_SERVICE_URL || "http://localhost:8000";
 61‚Üí    const stitchResponse = await fetch(`${stitchUrl}/stitch`, {
 62‚Üí      method: "POST",
 63‚Üí      body: formData,
 64‚Üí    });
 65‚Üí
 66‚Üí    if (!stitchResponse.ok) {
 67‚Üí      const errorText = await stitchResponse.text();
 68‚Üí      throw new Error(`Stitch service error: ${errorText}`);
 69‚Üí    }
 70‚Üí
 71‚Üí    // Get panorama blob from stitch service
 72‚Üí    const panoramaBlob = await stitchResponse.blob();
 73‚Üí    const panoramaPath = `panoramas/${sessionId}.jpg`;
 74‚Üí
 75‚Üí    // Upload panorama to Supabase Storage
 76‚Üí    const panoramaBuffer = await panoramaBlob.arrayBuffer();
 77‚Üí    const { error: uploadError } = await supabase.storage
 78‚Üí      .from("panoramas")
 79‚Üí      .upload(panoramaPath, panoramaBuffer, {
 80‚Üí        contentType: "image/jpeg",
 81‚Üí        upsert: true,
 82‚Üí      });
 83‚Üí
 84‚Üí    if (uploadError) {
 85‚Üí      throw new Error(`Panorama upload failed: ${uploadError.message}`);
 86‚Üí    }
 87‚Üí
 88‚Üí    // Get public URL for the panorama
 89‚Üí    const { data: urlData } = supabase.storage
 90‚Üí      .from("panoramas")
 91‚Üí      .getPublicUrl(panoramaPath);
 92‚Üí
 93‚Üí    // Update session with panorama info
 94‚Üí    await supabase
 95‚Üí      .from("capture_sessions")
 96‚Üí      .update({
 97‚Üí        status: "stitched",
 98‚Üí        panorama_storage_path: panoramaPath,
 99‚Üí        panorama_url: urlData.publicUrl,
100‚Üí      })
101‚Üí      .eq("id", sessionId);
102‚Üí
103‚Üí    return NextResponse.json({
104‚Üí      sessionId,
105‚Üí      panoramaPath,
106‚Üí      panoramaUrl: urlData.publicUrl,
107‚Üí    });
108‚Üí  } catch (err) {
109‚Üí    await supabase
110‚Üí      .from("capture_sessions")
111‚Üí      .update({ status: "failed" })
112‚Üí      .eq("id", sessionId);
113‚Üí
114‚Üí    return NextResponse.json(
115‚Üí      { error: err instanceof Error ? err.message : "Stitching failed" },
116‚Üí      { status: 500 }
117‚Üí    );
118‚Üí  }
119‚Üí}
