  6‚Üíexport async function POST(request: Request) {
  7‚Üí  const supabase = await createClient();
  8‚Üí  const { data: { user } } = await supabase.auth.getUser();
  9‚Üí
 10‚Üí  if (!user) {
 11‚Üí    return NextResponse.json({ error: "Unauthorized" }, { status: 401 });
 12‚Üí  }
 13‚Üí
 14‚Üí  const { sessionId, title } = await request.json();
 15‚Üí
 16‚Üí  if (!sessionId) {
 17‚Üí    return NextResponse.json({ error: "sessionId required" }, { status: 400 });
 18‚Üí  }
 19‚Üí
 20‚Üí  // Get session with panorama URL
 21‚Üí  const { data: session } = await supabase
 22‚Üí    .from("capture_sessions")
 23‚Üí    .select("*")
 24‚Üí    .eq("id", sessionId)
 25‚Üí    .eq("user_id", user.id)
 26‚Üí    .single();
 27‚Üí
 28‚Üí  if (!session || !session.panorama_url) {
 29‚Üí    return NextResponse.json(
 30‚Üí      { error: "Session not found or panorama not ready" },
 31‚Üí      { status: 404 }
 32‚Üí    );
 33‚Üí  }
 34‚Üí
 35‚Üí  try {
 36‚Üí    // Update session status
 37‚Üí    await supabase
 38‚Üí      .from("capture_sessions")
 39‚Üí      .update({ status: "generating" })
 40‚Üí      .eq("id", sessionId);
 41‚Üí
 42‚Üí    // 1. Prepare upload on World Labs
 43‚Üí    const displayName = title || "Space Clone Capture";
 44‚Üí    const { media_asset, upload_url } = await worldlabs.prepareUpload(
 45‚Üí      `panorama_${sessionId}.jpg`,
 46‚Üí      "jpg"
 47‚Üí    );
 48‚Üí
 49‚Üí    // 2. Download panorama from Supabase and upload to World Labs
 50‚Üí    const { data: panoramaBlob } = await supabase.storage
 51‚Üí      .from("panoramas")
 52‚Üí      .download(session.panorama_storage_path!);
 53‚Üí
 54‚Üí    if (!panoramaBlob) {
 55‚Üí      throw new Error("Failed to download panorama from storage");
 56‚Üí    }
 57‚Üí
 58‚Üí    await worldlabs.uploadFile(upload_url, panoramaBlob, "image/jpeg");
 59‚Üí
 60‚Üí    // 3. Generate world
 61‚Üí    const operation = await worldlabs.generateWorld({
 62‚Üí      displayName,
 63‚Üí      mediaAssetId: media_asset.media_asset_id,
 64‚Üí      isPano: true,
 65‚Üí    });
 66‚Üí
 67‚Üí    // Extract operation ID from operation name (format: "operations/{id}")
 68‚Üí    const operationId = operation.name.replace("operations/", "");
 69‚Üí
 70‚Üí    // 4. Create world record in DB
 71‚Üí    const shareSlug = nanoid(10);
 72‚Üí    const { data: world, error: worldError } = await supabase
 73‚Üí      .from("worlds")
 74‚Üí      .insert({
 75‚Üí        user_id: user.id,
 76‚Üí        session_id: sessionId,
 77‚Üí        operation_id: operationId,
 78‚Üí        status: "generating",
 79‚Üí        title: displayName,
 80‚Üí        is_public: false,
 81‚Üí        share_slug: shareSlug,
 82‚Üí      })
 83‚Üí      .select("id")
 84‚Üí      .single();
 85‚Üí
 86‚Üí    if (worldError || !world) {
 87‚Üí      throw new Error("Failed to create world record");
 88‚Üí    }
 89‚Üí
 90‚Üí    return NextResponse.json({
 91‚Üí      worldId: world.id,
 92‚Üí      operationId,
 93‚Üí      shareSlug,
 94‚Üí    });
 95‚Üí  } catch (err) {
 96‚Üí    await supabase
 97‚Üí      .from("capture_sessions")
 98‚Üí      .update({ status: "failed" })
 99‚Üí      .eq("id", sessionId);
100‚Üí
101‚Üí    return NextResponse.json(
102‚Üí      { error: err instanceof Error ? err.message : "Generation failed" },
103‚Üí      { status: 500 }
104‚Üí    );
105‚Üí  }
106‚Üí}
