  1‚Üíimport { NextRequest, NextResponse } from "next/server";
  2‚Üíimport { createClient } from "@/lib/supabase/server";
  3‚Üíimport * as worldlabs from "@/lib/worldlabs";
  4‚Üí
  5‚Üíexport async function GET(request: NextRequest) {
  6‚Üí  const supabase = await createClient();
  7‚Üí  const { data: { user } } = await supabase.auth.getUser();
  8‚Üí
  9‚Üí  if (!user) {
 10‚Üí    return NextResponse.json({ error: "Unauthorized" }, { status: 401 });
 11‚Üí  }
 12‚Üí
 13‚Üí  const worldId = request.nextUrl.searchParams.get("worldId");
 14‚Üí
 15‚Üí  if (!worldId) {
 16‚Üí    return NextResponse.json({ error: "worldId required" }, { status: 400 });
 17‚Üí  }
 18‚Üí
 19‚Üí  // Get world record
 20‚Üí  const { data: world } = await supabase
 21‚Üí    .from("worlds")
 22‚Üí    .select("*")
 23‚Üí    .eq("id", worldId)
 24‚Üí    .eq("user_id", user.id)
 25‚Üí    .single();
 26‚Üí
 27‚Üí  if (!world) {
 28‚Üí    return NextResponse.json({ error: "World not found" }, { status: 404 });
 29‚Üí  }
 30‚Üí
 31‚Üí  // If already ready or failed, return current status
 32‚Üí  if (world.status === "ready" || world.status === "failed") {
 33‚Üí    return NextResponse.json({
 34‚Üí      status: world.status,
 35‚Üí      world,
 36‚Üí    });
 37‚Üí  }
 38‚Üí
 39‚Üí  // Poll World Labs operation
 40‚Üí  if (!world.operation_id) {
 41‚Üí    return NextResponse.json({ error: "No operation ID" }, { status: 500 });
 42‚Üí  }
 43‚Üí
 44‚Üí  try {
 45‚Üí    const operation = await worldlabs.getOperation(world.operation_id);
 46‚Üí
 47‚Üí    if (operation.done && operation.response) {
 48‚Üí      const wlWorld = operation.response.world;
 49‚Üí      const viewerUrl = `https://marble.worldlabs.ai/world/${wlWorld.world_id}`;
 50‚Üí
 51‚Üí      // Update world record with final data
 52‚Üí      await supabase
 53‚Üí        .from("worlds")
 54‚Üí        .update({
 55‚Üí          status: "ready",
 56‚Üí          world_labs_id: wlWorld.world_id,
 57‚Üí          viewer_url: viewerUrl,
 58‚Üí          thumbnail_url: wlWorld.assets.thumbnail_url || null,
 59‚Üí          panorama_url: wlWorld.assets.panorama_url || null,
 60‚Üí          splat_url: wlWorld.assets.splat_url || null,
 61‚Üí          mesh_url: wlWorld.assets.mesh_url || null,
 62‚Üí        })
 63‚Üí        .eq("id", worldId);
 64‚Üí
 65‚Üí      // Also update session status
 66‚Üí      if (world.session_id) {
 67‚Üí        await supabase
 68‚Üí          .from("capture_sessions")
 69‚Üí          .update({ status: "done" })
 70‚Üí          .eq("id", world.session_id);
 71‚Üí      }
 72‚Üí
 73‚Üí      const { data: updatedWorld } = await supabase
 74‚Üí        .from("worlds")
 75‚Üí        .select("*")
 76‚Üí        .eq("id", worldId)
 77‚Üí        .single();
 78‚Üí
 79‚Üí      return NextResponse.json({
 80‚Üí        status: "ready",
 81‚Üí        world: updatedWorld,
 82‚Üí      });
 83‚Üí    }
 84‚Üí
 85‚Üí    // Still generating
 86‚Üí    const progress = operation.metadata?.progress;
 87‚Üí    return NextResponse.json({
 88‚Üí      status: "generating",
 89‚Üí      progress: {
 90‚Üí        state: progress?.status || "IN_PROGRESS",
 91‚Üí        percent: progress?.percent_complete || null,
 92‚Üí      },
 93‚Üí    });
 94‚Üí  } catch (err) {
 95‚Üí    // If World Labs API fails, mark as failed
 96‚Üí    const errorMsg = err instanceof Error ? err.message : "Status check failed";
 97‚Üí
 98‚Üí    if (errorMsg.includes("404") || errorMsg.includes("not found")) {
 99‚Üí      await supabase
100‚Üí        .from("worlds")
101‚Üí        .update({ status: "failed" })
102‚Üí        .eq("id", worldId);
103‚Üí
104‚Üí      return NextResponse.json({ status: "failed", error: errorMsg });
105‚Üí    }
106‚Üí
107‚Üí    return NextResponse.json(
108‚Üí      { error: errorMsg },
109‚Üí      { status: 500 }
110‚Üí    );
111‚Üí  }
112‚Üí}
