 1‚Üíimport { NextResponse } from "next/server";
 2‚Üíimport { createClient } from "@/lib/supabase/server";
 3‚Üíimport { nanoid } from "nanoid";
 4‚Üí
 5‚Üíexport async function POST(request: Request) {
 6‚Üí  const supabase = await createClient();
 7‚Üí  const { data: { user } } = await supabase.auth.getUser();
 8‚Üí
 9‚Üí  if (!user) {
10‚Üí    return NextResponse.json({ error: "Unauthorized" }, { status: 401 });
11‚Üí  }
12‚Üí
13‚Üí  const formData = await request.formData();
14‚Üí  const files = formData.getAll("photos") as File[];
15‚Üí
16‚Üí  if (files.length < 2 || files.length > 20) {
17‚Üí    return NextResponse.json(
18‚Üí      { error: `Expected 2-20 photos, got ${files.length}` },
19‚Üí      { status: 400 }
20‚Üí    );
21‚Üí  }
22‚Üí
23‚Üí  const sessionId = nanoid(12);
24‚Üí  const storagePath = `photos/${user.id}/${sessionId}`;
25‚Üí
26‚Üí  // Create capture session record
27‚Üí  const { data: session, error: dbError } = await supabase
28‚Üí    .from("capture_sessions")
29‚Üí    .insert({
30‚Üí      user_id: user.id,
31‚Üí      status: "uploading",
32‚Üí      photo_count: files.length,
33‚Üí      photos_storage_path: storagePath,
34‚Üí    })
35‚Üí    .select("id")
36‚Üí    .single();
37‚Üí
38‚Üí  if (dbError || !session) {
39‚Üí    return NextResponse.json({ error: "Failed to create session" }, { status: 500 });
40‚Üí  }
41‚Üí
42‚Üí  // Upload each photo to Supabase Storage
43‚Üí  const uploadErrors: string[] = [];
44‚Üí
45‚Üí  await Promise.all(
46‚Üí    files.map(async (file, i) => {
47‚Üí      const ext = file.name.split(".").pop() || "jpg";
48‚Üí      const path = `${storagePath}/${String(i).padStart(2, "0")}.${ext}`;
49‚Üí      const buffer = await file.arrayBuffer();
50‚Üí
51‚Üí      const { error } = await supabase.storage
52‚Üí        .from("photos")
53‚Üí        .upload(path, buffer, {
54‚Üí          contentType: file.type || "image/jpeg",
55‚Üí          upsert: false,
56‚Üí        });
57‚Üí
58‚Üí      if (error) uploadErrors.push(`File ${i}: ${error.message}`);
59‚Üí    })
60‚Üí  );
61‚Üí
62‚Üí  if (uploadErrors.length > 0) {
63‚Üí    await supabase
64‚Üí      .from("capture_sessions")
65‚Üí      .update({ status: "failed" })
66‚Üí      .eq("id", session.id);
67‚Üí
68‚Üí    return NextResponse.json(
69‚Üí      { error: "Upload failed", details: uploadErrors },
70‚Üí      { status: 500 }
71‚Üí    );
72‚Üí  }
73‚Üí
74‚Üí  // Mark session as uploaded
75‚Üí  await supabase
76‚Üí    .from("capture_sessions")
77‚Üí    .update({ status: "uploading" })
78‚Üí    .eq("id", session.id);
79‚Üí
80‚Üí  return NextResponse.json({ sessionId: session.id, storagePath });
81‚Üí}
