  1‚Üí"use client";
  2‚Üí
  3‚Üíimport { useState, useRef, useCallback } from "react";
  4‚Üíimport { cn } from "@/lib/utils";
  5‚Üí
  6‚Üí/**
  7‚Üí * BottomSheet -- draggable bottom sheet for the world viewer.
  8‚Üí *
  9‚Üí * Design decisions:
 10‚Üí * - Starts at a "peek" height showing title + actions.
 11‚Üí * - Draggable handle lets users pull it to full or dismiss to peek.
 12‚Üí * - Uses transform for smooth 60fps animation (GPU-composited).
 13‚Üí * - Glassmorphism background to let the 3D viewer peek through.
 14‚Üí *
 15‚Üí * Accessibility:
 16‚Üí * - Handle has role="slider" for screen reader drag semantics.
 17‚Üí * - Content is always reachable via keyboard (no hidden states).
 18‚Üí * - Focus trap when expanded (future enhancement).
 19‚Üí */
 20‚Üí
 21‚Üíinterface BottomSheetProps {
 22‚Üí  children: React.ReactNode;
 23‚Üí  className?: string;
 24‚Üí}
 25‚Üí
 26‚Üíexport function BottomSheet({ children, className }: BottomSheetProps) {
 27‚Üí  const [expanded, setExpanded] = useState(false);
 28‚Üí  const sheetRef = useRef<HTMLDivElement>(null);
 29‚Üí  const startY = useRef(0);
 30‚Üí  const currentY = useRef(0);
 31‚Üí
 32‚Üí  const handleTouchStart = useCallback((e: React.TouchEvent) => {
 33‚Üí    startY.current = e.touches[0].clientY;
 34‚Üí  }, []);
 35‚Üí
 36‚Üí  const handleTouchMove = useCallback((e: React.TouchEvent) => {
 37‚Üí    currentY.current = e.touches[0].clientY;
 38‚Üí    const diff = startY.current - currentY.current;
 39‚Üí
 40‚Üí    if (sheetRef.current) {
 41‚Üí      /* Apply a damped translation during drag for tactile feedback */
 42‚Üí      const dampedDiff = diff * 0.4;
 43‚Üí      const clampedTranslate = Math.max(-20, Math.min(100, dampedDiff));
 44‚Üí      sheetRef.current.style.transform = `translateY(${-clampedTranslate}px)`;
 45‚Üí    }
 46‚Üí  }, []);
 47‚Üí
 48‚Üí  const handleTouchEnd = useCallback(() => {
 49‚Üí    const diff = startY.current - currentY.current;
 50‚Üí
 51‚Üí    /* Swipe up threshold: 60px to expand */
 52‚Üí    if (diff > 60) {
 53‚Üí      setExpanded(true);
 54‚Üí    }
 55‚Üí    /* Swipe down threshold: 60px to collapse */
 56‚Üí    if (diff < -60) {
 57‚Üí      setExpanded(false);
 58‚Üí    }
 59‚Üí
 60‚Üí    /* Reset inline transform; let CSS take over */
 61‚Üí    if (sheetRef.current) {
 62‚Üí      sheetRef.current.style.transform = "";
 63‚Üí    }
 64‚Üí  }, []);
 65‚Üí
 66‚Üí  return (
 67‚Üí    <div
 68‚Üí      ref={sheetRef}
 69‚Üí      className={cn(
 70‚Üí        "fixed inset-x-0 bottom-0 z-50",
 71‚Üí        "glass-surface rounded-t-2xl safe-bottom",
 72‚Üí        "transition-[max-height] duration-500 ease-[cubic-bezier(0.16,1,0.3,1)]",
 73‚Üí        expanded ? "max-h-[70vh]" : "max-h-[180px]",
 74‚Üí        className
 75‚Üí      )}
 76‚Üí      onTouchStart={handleTouchStart}
